INSERT INTO `auteurs` (`Ref_auteur`, `Nom_auteur`, `Bio_auteur`, `Hyperlink_auteur`) VALUES
(1, 'Cheikh A. Bamba', 'Cheikh Ahmadou Bamba Mbacké (Aḥmad ibn Muḥammad ibn Ḥabīb Allāh) dit Khadimou al-Rassoul (en arabe : « serviteur du Prophète » et Serigne Touba (chef religieux, fondateur de Touba), né vers 1853 à Mbacké (anciennement Mbacké-Baol), décédé le 19 juillet 1927 à Diourbel au Sénégal, est un théologien, juriste musulman et soufi. Il est l’une des figures les plus importantes de l\'islam de la région en qualité de fondateur de la confrérie des Mourides. Il fut également un grand poète (en langue arabe). Il marqua l\'histoire de son pays de par son Rayonnement polarisateur, dans le contexte de la colonisation française. (…)', 'https://fr.wikipedia.org/wiki/Ahmadou_Bamba'),
(2, 'Cheikh Muhammad Moustapha Mbacké', NULL, NULL),
(3, 'Cheikh Muhammad Bachir Mbacké', NULL, NULL),
(4, 'S. El Hadj Mbacké Khelcom', NULL, NULL),
(5, 'Cheikh Moussa Ka', NULL, NULL),
(6, 'Cheikh Gueye', 'Cheikh Guèye est docteur en géographie de l\'université Louis-Pasteur de Strasbourg. Il suit depuis une dizaine d\'années l\'évolution de la ville sainte de la confrérie des mourides à laquelle il appartient. Il a mené d\'autres recherches sur les villes et s\'intéresse aux concepts de territoire, de frontière et d\'intégration. Il est également l\'un des contributeurs à la recherche initiée par l\'UNRISD (Institut des Nations Unies pour le développement) sur les technologies de l\'information et de la communication au Sénégal. Cheikh Guèye est aujourd\'hui chercheur à l\'ENDA Tiers-monde (Dakar).', NULL),
(7, 'Mouhamadou Mbacké Diouf', NULL, NULL),
(8, 'Abdoul Aziz Mbacké Majalis', NULL, NULL),
(9, 'Cheikh Abdoul Ahad Mbacké', NULL, NULL),
(10, 'Cheikh Mouhamadou Fadl', NULL, NULL),
(11, 'Serigne Sam Mbaye', NULL, NULL),
(12, 'Serigne Mayib Gueye', NULL, NULL);
